// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

contract Price {
    /// @notice ChainLink priceFeeds per token pair
    mapping(string => address) public priceFeeds;

    constructor(string[] memory pairs, address[] memory adresses) {
        require(pairs.length == adresses.length, "Invalid constructor params");
        for (uint256 i = 0; i < pairs.length; i++) {
            priceFeeds[pairs[i]] = adresses[i];
        }
    }

    /// @notice Returns latest price
    function getLatestPrice(string calldata pair) public view returns (int256) {
        (, int256 price, , , ) = AggregatorV3Interface(priceFeeds[pair]).latestRoundData();
        return price;
    }
}

